mapper.select:

select 

from fri_pay_advice
where close_no = #{closeNo,jdbcType=VARCHAR}
  and close_paid_seq = #{closePaidSeq,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and risk_no = #{riskNo,jdbcType=VARCHAR}
  and treaty_year = #{treatyYear,jdbcType=VARCHAR}
  and treaty_no = #{treatyNo,jdbcType=VARCHAR}
  and rin_com_id = #{rinComId,jdbcType=VARCHAR}
  and broker_id = #{brokerId,jdbcType=VARCHAR}
  and rin_com_id_OLD = #{rinComIdOld,jdbcType=VARCHAR}
  and broker_id_OLD = #{brokerIdOld,jdbcType=VARCHAR}

mapper.delete:

delete from fri_pay_advice
where close_no = #{closeNo,jdbcType=VARCHAR}
  and close_paid_seq = #{closePaidSeq,jdbcType=VARCHAR}
  and policy_no = #{policyNo,jdbcType=VARCHAR}
  and risk_no = #{riskNo,jdbcType=VARCHAR}
  and treaty_year = #{treatyYear,jdbcType=VARCHAR}
  and treaty_no = #{treatyNo,jdbcType=VARCHAR}
  and rin_com_id = #{rinComId,jdbcType=VARCHAR}
  and broker_id = #{brokerId,jdbcType=VARCHAR}
  and rin_com_id_OLD = #{rinComIdOld,jdbcType=VARCHAR}
  and broker_id_OLD = #{brokerIdOld,jdbcType=VARCHAR}
