mapper.select:

select 

from fri_policy_addition
where policy_no = #{policyNo,jdbcType=VARCHAR}
  and endorse_no = #{endorseNo,jdbcType=VARCHAR}
  and addr_no = #{addrNo,jdbcType=SMALLINT}
  and prop_no = #{propNo,jdbcType=SMALLINT}
  and addition_seq = #{additionSeq,jdbcType=SMALLINT}
  and addition_no = #{additionNo,jdbcType=VARCHAR}

mapper.delete:

delete from fri_policy_addition
where policy_no = #{policyNo,jdbcType=VARCHAR}
  and endorse_no = #{endorseNo,jdbcType=VARCHAR}
  and addr_no = #{addrNo,jdbcType=SMALLINT}
  and prop_no = #{propNo,jdbcType=SMALLINT}
  and addition_seq = #{additionSeq,jdbcType=SMALLINT}
  and addition_no = #{additionNo,jdbcType=VARCHAR}

mapper.update:

update fri_policy_addition


    addition_name = #{additionName,jdbcType=VARCHAR},


    amt = #{amt,jdbcType=DECIMAL},


    prem = #{prem,jdbcType=DECIMAL},


    prercv_rate = #{prercvRate,jdbcType=DECIMAL},


    limit = #{limit,jdbcType=DECIMAL},


    limit_year = #{limitYear,jdbcType=DECIMAL},


    limit_rate = #{limitRate,jdbcType=DECIMAL},


    rate = #{rate,jdbcType=DECIMAL},


    deduct_rem = #{deductRem,jdbcType=VARCHAR},


    mercno = #{mercno,jdbcType=CHAR},


where policy_no = #{policyNo,jdbcType=VARCHAR}
  and endorse_no = #{endorseNo,jdbcType=VARCHAR}
  and addr_no = #{addrNo,jdbcType=SMALLINT}
  and prop_no = #{propNo,jdbcType=SMALLINT}
  and addition_seq = #{additionSeq,jdbcType=SMALLINT}
  and addition_no = #{additionNo,jdbcType=VARCHAR}


update fri_policy_addition
set addition_name = #{additionName,jdbcType=VARCHAR},
  amt = #{amt,jdbcType=DECIMAL},
  prem = #{prem,jdbcType=DECIMAL},
  prercv_rate = #{prercvRate,jdbcType=DECIMAL},
  limit = #{limit,jdbcType=DECIMAL},
  limit_year = #{limitYear,jdbcType=DECIMAL},
  limit_rate = #{limitRate,jdbcType=DECIMAL},
  rate = #{rate,jdbcType=DECIMAL},
  deduct_rem = #{deductRem,jdbcType=VARCHAR},
  mercno = #{mercno,jdbcType=CHAR}
where policy_no = #{policyNo,jdbcType=VARCHAR}
  and endorse_no = #{endorseNo,jdbcType=VARCHAR}
  and addr_no = #{addrNo,jdbcType=SMALLINT}
  and prop_no = #{propNo,jdbcType=SMALLINT}
  and addition_seq = #{additionSeq,jdbcType=SMALLINT}
  and addition_no = #{additionNo,jdbcType=VARCHAR}
